package myproject.common.utils;/** * 特征(二值函数) */class Feature {	/**	 * 事件	 */	String label;	/**	 * 事件发生的环境，	 */	String value;	/**	 * 特征函数	 *	 * @param label 类别	 * @param value 环境	 */	public Feature(String label, String value) {		this.label = label;		this.value = value;	}	@Override	public boolean equals(Object obj) {		Feature feature = (Feature) obj;		return this.label.equals(feature.label) && this.value.equals(feature.value);	}	@Override	public String toString() {		return "[" + label + ", " + value + "]";	}}